6474849505152535455565758596061626364656667686970717273747576777879808182838485868788899091929394959697import java.util.ArrayList;import java.util.Scanner;public class Main &#123; private static Scanner scanner = new Scanner(System.in); private static GroceryList groceryList = new GroceryList(); public static void main(String[] args) &#123; boolean quit = false; int choice = 0; printInstructions(); while(!quit) &#123; System.out.println(&quot;Enter your choice: &quot;); choice = scanner.nextInt(); scanner.nextLine(); switch (choice) &#123; case 0: printInstructions(); break; case 1: groceryList.printGroceryList(); break; case 2: addItem(); break; case 3: modifyItem(); break; case 4: removeItem(); break; case 5: searchForItem(); break; case 6: processArrayList(); case 7: quit = true; break; &#125; &#125; &#125; public static void printInstructions() &#123; System.out.println(&quot;
Press &quot;); System.out.println(&quot;	 0 - To print choice options.&quot;); System.out.println(&quot;	 1 - To print the list of grocery items.&quot;); System.out.println(&quot;	 2 - To add an item to the list.&quot;); System.out.println(&quot;	 3 - To modify an item in the list.&quot;); System.out.println(&quot;	 4 - To remove an item from the list.&quot;); System.out.println(&quot;	 5 - To search for an item in the list.&quot;); System.out.println(&quot;	 6 - To quit the application.&quot;); &#125; public static void addItem() &#123; System.out.print(&quot;Please enter the grocery item: &quot;); groceryList.addGroceryItem(scanner.nextLine()); &#125; public static void modifyItem() &#123; System.out.print(&quot;Current item name: &quot;); String itemNo = scanner.nextLine(); System.out.print(&quot;Enter new item: &quot;); String newItem = scanner.nextLine(); groceryList.modifyGroceryItem(itemNo, newItem); &#125; public static void removeItem() &#123; System.out.print(&quot;Enter item name: &quot;); String itemNo = scanner.nextLine(); groceryList.removeGroceryItem(itemNo); &#125; public static void searchForItem() &#123; System.out.print(&quot;Item to search for: &quot;); String searchItem = scanner.nextLine(); if (groceryList.onFile(searchItem)) &#123; System.out.println(&quot;Found &quot; + searchItem + &quot; in our grocery list&quot;); &#125; else &#123; System.out.println(searchItem + &quot; is not in the shopping list&quot;); &#125; &#125; // ä¸‰ç¨® è¤‡è£½ ArrayList çš„æ–¹æ³• public static void processArrayList() &#123; ArrayList&lt;String&gt; newArray = new ArrayList&lt;String&gt;(); newArray.addAll(groceryList.getGroceryList()); ArrayList&lt;String&gt; nextArray = new ArrayList&lt;String&gt;(groceryList.getGroceryList()); // æŠŠ ArrayList è½‰æ›æˆ Array String[] myArray = new String[groceryList.getGroceryList().size()]; myArray = groceryList.getGroceryList().toArray(myArray); &#125;&#125; ArrayList æŒ‘æˆ° é¡Œç›® Create a program that implements a simple mobile phone with the following capabilities. Able to store, modify, remove and query contact names. You will want to create a separate class for Contacts (name and phone number). Create a master class (MobilePhone) that holds the ArrayList of Contacts The MobilePhone class has the functionality listed above. Add a menu of options that are available. Options: Quit, print list of contacts, add new contact, update existing contact, remove contact and search/find contact. When adding or updating be sure to check if the contact already exists (use name) Be sure not to expose the inner workings of the Arraylist to MobilePhone e.g. no ints, no .get(i) etc MobilePhone should do everything with Contact objects only. ç­”æ¡ˆ æ–°å¢ Contact.java 123456789101112131415161718192021public class Contact &#123; private String name; private String phoneNumber; public Contact(String name, String phoneNumber) &#123; this.name = name; this.phoneNumber = phoneNumber; &#125; public String getName() &#123; return name; &#125; public String getPhoneNumber() &#123; return phoneNumber; &#125; public static Contact createContact(String name, String phoneNumber) &#123; return new Contact(name, phoneNumber); &#125;&#125; æ–°å¢ MobilePhone.java 123456789101112131415161718192021222324252627282930313233343536373839404142434445464748495051525354555657585960616263646566676869707172737475767778import java.util.ArrayList;public class MobilePhone &#123; private String myNumber; private ArrayList&lt;Contact&gt; myContacts; public MobilePhone(String myNumber) &#123; this.myNumber = myNumber; this.myContacts = new ArrayList&lt;Contact&gt;(); &#125; public boolean addNewContact(Contact contact) &#123; if (findContact(contact.getName()) &gt;= 0) &#123; System.out.println(&quot;Contact is already on file&quot;); return false; &#125; myContacts.add(contact); return true; &#125; public boolean updateContact(Contact oldContact, Contact newContact) &#123; int foundPosition = findContact(oldContact); if (foundPosition &lt; 0) &#123; System.out.println(oldContact.getName() + &quot;, was not found.&quot;); return false; &#125; else if (findContact(newContact.getName()) != -1) &#123; System.out.println(&quot;Contact with name &quot; + newContact.getName() + &quot; already exist. Update was not successful.&quot;); return false; &#125; this.myContacts.set(foundPosition, newContact); System.out.println(oldContact.getName() + &quot;, was replaced with &quot; + newContact.getName()); return true; &#125; public boolean removeContact(Contact contact) &#123; int foundPosition = findContact(contact); if (foundPosition &lt; 0) &#123; System.out.println(contact.getName() + &quot;, was not found.&quot;); return false; &#125; this.myContacts.remove(foundPosition); System.out.println(contact.getName() + &quot;, was deleted.&quot;); return true; &#125; private int findContact(Contact contact) &#123; return this.myContacts.indexOf(contact); &#125; private int findContact(String contactName) &#123; for (int i = 0; i &lt; this.myContacts.size(); i++) &#123; Contact contact = this.myContacts.get(i); if (contact.getName().equals(contactName)) &#123; return i; &#125; &#125; return -1; &#125; public Contact queryContact(String name) &#123; int position = findContact(name); if (position &gt;= 0) &#123; return this.myContacts.get(position); &#125; return null; &#125; public void printContacts() &#123; System.out.println(&quot;Contact List&quot;); for (int i = 0; i &lt; this.myContacts.size(); i++) &#123; System.out.println((i + 1) + &quot;.&quot; + this.myContacts.get(i).getName() + &quot; -&gt; &quot; + this.myContacts.get(i).getPhoneNumber()); &#125; &#125;&#125; ä¿®æ”¹ Main.java 123456789101112131415161718192021222324252627282930313233343536373839404142434445464748495051525354555657585960616263646566676869707172737475767778798081828384858687888990919293949596979899100101102103104105106107108109110111112113114115116117118119120import java.util.Scanner;public class Main &#123; private static Scanner scanner = new Scanner(System.in); private static MobilePhone mobilePhone = new MobilePhone(&quot;0039 330 4404&quot;); public static void main(String[] args) &#123; boolean quit = false; startPhone(); printActions(); while (!quit) &#123; System.out.println(&quot;
Enter action: (6 to show available actions)&quot;); int action = scanner.nextInt(); scanner.nextLine(); switch (action) &#123; case 0: System.out.println(&quot;
Shutting down...&quot;); quit = true; break; case 1: mobilePhone.printContacts(); break; case 2: addNewContact(); break; case 3: updateContact(); break; case 4: removeContact(); break; case 5: queryContact(); break; case 6: printActions(); break; &#125; &#125; &#125; private static void updateContact() &#123; System.out.println(&quot;Enter existing contact name: &quot;); String name = scanner.nextLine(); Contact existingContactRecord = mobilePhone.queryContact(name); if (existingContactRecord == null) &#123; System.out.println(&quot;Contact not found&quot;); return; &#125; System.out.println(&quot;Enter new contact name: &quot;); String newName = scanner.nextLine(); System.out.println(&quot;Enter new contact phone number: &quot;); String newNumber = scanner.nextLine(); Contact newContact = Contact.createContact(newName, newNumber); if (mobilePhone.updateContact(existingContactRecord, newContact)) &#123; System.out.println(&quot;Successfully updated record&quot;); &#125; else &#123; System.out.println(&quot;Error updating record&quot;); &#125; &#125; private static void removeContact() &#123; System.out.println(&quot;Enter existing contact name: &quot;); String name = scanner.nextLine(); Contact existingContactRecord = mobilePhone.queryContact(name); if (existingContactRecord == null) &#123; System.out.println(&quot;Contact not found&quot;); return; &#125; if (mobilePhone.removeContact(existingContactRecord)) &#123; System.out.println(&quot;Successfully deleted&quot;); &#125; else &#123; System.out.println(&quot;Error deleting contact&quot;); &#125; &#125; private static void queryContact() &#123; System.out.println(&quot;Enter existing contact name: &quot;); String name = scanner.nextLine(); Contact existingContactRecord = mobilePhone.queryContact(name); if (existingContactRecord == null) &#123; System.out.println(&quot;Contact not found&quot;); return; &#125; System.out.println(&quot;Name: &quot; + existingContactRecord.getName() + &quot; phone number is &quot; + existingContactRecord.getPhoneNumber()); &#125; private static void addNewContact() &#123; System.out.println(&quot;Enter new contact name: &quot;); String name = scanner.nextLine(); System.out.println(&quot;Enter phone number: &quot;); String phone = scanner.nextLine(); Contact newContact = Contact.createContact(name, phone); if (mobilePhone.addNewContact(newContact)) &#123; System.out.println(&quot;New contact added: &quot; + name + &quot;, phone = &quot; + phone); &#125; else &#123; System.out.println(&quot;Cannot add, &quot; + name + &quot; already on file&quot;); &#125; &#125; private static void startPhone() &#123; System.out.println(&quot;Starting phone...&quot;); &#125; private static void printActions() &#123; System.out.println(&quot;
Available actions:
